mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT b.club_num,
REPLACE(REPLACE(b.club_title,'<','&lt;'),'>','&gt;') club_title,
b.club_leader, b.club_date, b.club_reg_date, b.club_code, b.club_limit, b.club_pre, b.club_hit, b.club_gender, b.club_recruit, b.club_img, b.club_img_name, b.club_age, b.club_region, b.club_name, m.id, m.nickname FROM club_board b JOIN member m ON b.club_leader = m.mem_num
b.club_code = #{keyfield}
ORDER BY b.club_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE club_board SET club_title=#{club_title}, club_code=#{club_code}, club_content=#{club_content}, club_limit=#{club_limit}, club_name=#{club_name}, club_age=#{club_age}, club_gender=#{club_gender}, club_recruit=#{club_recruit}, club_date=#{club_date}, club_region=#{club_region},
club_img=#{club_img}, club_img_name=#{club_img_name},
club_modify_date=SYSDATE WHERE club_num=#{club_num}
