mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT
f.used_num, f.mem_num, u.title, u.status, u.kind, u.category, u.price FROM fav f JOIN used_board u ON f.used_num=u.used_num WHERE f.mem_num = #{mem_num} and f.used_num IS NOT NULL ORDER BY f.used_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}
