mapper.select:
		select count(*) 
		  from 
		norder n join norder_detail d
		using(order_num) 
		where n.mem_num=#{mem_num}
		
			
  				and n.order_num = #{keyword} 
  			
  			
  				and d.product_name like '%' || #{keyword} || '%'

select * from (select a.*, rownum rnum from (select distinct(order_num),mem_num,total_name,order_total,status,receive_name, receive_post,receive_address1,receive_address2,receive_phone, notice,reg_date from norder join norder_detail using(order_num) where mem_num=#{mem_num}
and n.order_num = #{keyword}
and d.product_name like '%' || #{keyword} || '%'
order by order_num desc)a)
WHERE rnum >= #{start} AND rnum <= #{end}

select * from (select a.*, rownum rnum from (select product_num, product_name, product_price, product_total, order_quantity, order_num from norder_detail join norder using(order_num) where mem_num=#{mem_num} and wait_status=#{wait_status}
and n.order_num = #{keyword}
and d.product_name like '%' || #{keyword} || '%'
order by order_num desc)a)
WHERE rnum >= #{start} AND rnum <= #{end}
