mapper.insert:
insert into product (product_num, name, kind, sub_category, title, price1, price2, quantity, req_quantity, photo1,photo1_name,
photo2,photo2_name,
photo3,photo3_name,
company, origin, status, deadline,reg_date
,modify_date
) values (product_seq.nextval,#{name},#{kind},#{sub_category},#{title}, #{price1},#{price2},#{quantity},#{req_quantity},#{photo1},#{photo1_name},
#{photo2},#{photo2_name}, photo2,photo2_name,
#{photo3},#{photo3_name},
#{company},#{origin},#{status},#{deadline},#{reg_date}
,#{modify_date}
)
